NDefines.NAI.DIPLOMATIC_ACTION_GUARANTEE_POWERBALANCE_FACTOR = 80				-- AI scoring for guarantee action is increased by this if a nation blocks a powerbalance threat.
NDefines.NAI.DIPLOMATIC_ACTION_GIFT_POWERBALANCE_FACTOR_AI = 100				-- AI willingness to send gifts to AI nations fighting power balance threat is modulated by this.
