NDefines.NAI.DIPLOMATIC_INTEREST_DISTANCE = 100									-- If border distance is greater than this, diplomatic AI will have less interest in the country
NDefines.NAI.CONQUEST_INTEREST_DISTANCE = 300 									-- Beyond this range, AI is less interested in conquest of provinces.
																				-- Rosth: assuming this refers to range from the capital, otherwise this wouldn't make much sense. Vanilla value is 100. 
NDefines.NAI.FORCE_MARCH_MIN_SIZE = 5											-- AI will not force march units with less regiments than this
NDefines.NAI.FORCE_MARCH_ALWAYS_SIZE = 10000									-- AI will always try to force march units of at least this size (disabled)
NDefines.NAI.MAX_BUILDING_COST_INCOME_MONTHS = 100								-- AI will not save up for a building that costs more than their monthly income * this, if there are cheaper alternatives
NDefines.NAI.PURSUE_DISTANCE = 70												-- AI will not pursue armies retreating to a province further away than this.
																				-- Rosth: "distance" refers to the distance between a province in which the stack is currently in and the province to which the enemy stack is retreating. For the reference, 100 is about the distance from Paris to Guiana/Vales/Westfalen. 70 is about the distance from Paris to Haut-Poetou/Milieu/Limburg. This is meant to stop the AI from chasing around retreating stacks while wasting time and suffering attrition and make it spend that time to actually siege provinces.
NDefines.NAI.CALL_IN_ALLIES_POWER_RATIO = 4										-- AI will only call in allies in an offensive war if their military power ratio to the enemy is less than this
