NDefines.NAI.DIPLOMATIC_ACTION_TRIBUTARY_EMPIRE_FACTOR = 10						-- AI scoring for establishing Tributary States is increased by this if actor is Celestial Emperor or has horde government with rank Empire.
NDefines.NAI.ALLIANCE_DESIRE_TOO_MANY_RELATIONS = -20							-- AI desire/acceptance for alliance when it has or will get too many relations. Multiplies with number of relations above limit.
NDefines.NAI.SUPPORT_INDEPENDENCE_DESIRE_TOO_MANY_RELATIONS = -20				-- AI desire/acceptance for supporting independence when it has or will get too many relations. Multiplies with number of relations above limit.
NDefines.NAI.MARRIAGE_DESIRE_TOO_MANY_RELATIONS = -50							-- AI desire/acceptance for royal marriange when it has or will get too many relations. Multiplies with number of relations above limit.
NDefines.NAI.GUARANTEE_DESIRE_TOO_MANY_RELATIONS = -50							-- AI desire for guaranteeing when it has or will get too many relations. Multiplies with number of relations above limit.

NDefines.NAI.INVADING_BRAVERY = 1												-- If (defender strength) / (invader strength) > INVADING_BRAVERY, the AI won't attempt a naval invasion.
NDefines.NAI.INVADING_MAX_AWAY_RATIO = 1										-- AI will never send more than this ratio to invade (measured in strength).
																				-- Rosth: was 0.5 in 2.52.06. Changed to 1 under the assumption that if the AI can launch a naval invasion it has naval superiority to begin with and there is no reason to not use as many troops as possible.
NDefines.NAI.HOME_FLEET_MAX_RATIO = 0.85										-- Maximum proportion of warships in home fleet.
NDefines.NAI.TRADE_POLICY_PROPAGATE_RELIGION_SCORE = 50							-- Score for propagate religion trade policy (if selectable)
NDefines.NAI.TRADE_POLICY_PREPARING_FOR_WAR_PER_PROVINCE_SCORE = 6				-- Score per enemy province while preparing war for attack bonus trade policy (if selectable)
NDefines.NAI.TRADE_POLICY_AT_WAR_PER_PROVINCE_SCORE = 5.5						-- Score per enemy province while preparing war for attack bonus trade policy (if selectable)
NDefines.NAI.TRADE_POLICY_MAXIMIZE_PROFITS_SCORE = 15							-- Score for maximize profit trade policy
NDefines.NAI.TRADE_POLICY_IMPROVE_RELATIONS_PER_COUNTRY_SCORE = 5				-- Score per country with merchant for improve relations policy
NDefines.NAI.TRADE_POLICY_SPY_OFFENCE_PER_COUNTRY_SCORE = 5						-- Score per country with merchant for spy offence policy
NDefines.NAI.DRILL_BONUS_WAR_READINESS = 0.08									-- Bonus for war readiness of drilled units
NDefines.NAI.MONTHS_STOP_DRILLING_BEFORE_WAR = 5								-- Months before a prepared war to stop drilling.
																				-- Rosth: in early patches the AI garthered troops on a border with a country it wanted to declare on, but that doesn't seem to be the case anymore (at least not in 1.29). Still, extra 2 months to regain morale wouldn't hurt (and also if they do somehow decide to preemptively move their armies - all the better).
NDefines.NAI.DRILLING_BUDGET_OF_SURPLUS = 0.75									-- Max amount of surplus to be used for drilling
NDefines.NAI.DRILLING_DEBT_SURPLUS_RATIO_THRESHOLD = 0.025						-- AI will only consider drilling if Surplus / Debt ratio is above
NDefines.NAI.DRILLING_ACCEPTABLE_THREAT_REDUCTION = 100							-- Acceptable threat reduction for drilling
NDefines.NAI.DANGEROUS_ESTATE_INFLUENCE_BUFFER = 100							-- AI will assign estates up to ESTATE_DANGER_THRESHOLD minus this (See ai_territory_modifier)
NDefines.NAI.ADVISOR_MIN_SKILL_RELUCTANT_FIRE = 3								-- AI will be reuluctant to fire advisors with skill above this (due to prior investment)
NDefines.NAI.ADVISOR_PROMOTION_AGE_CUTOFF = 40									-- AI will not promote advisors above this age
NDefines.NAI.MIN_FORCE_LIMIT_SHARE_REGION_ASSIGN = 0.1							-- AI will only assign armies larger that this to a region
NDefines.NAI.MAX_ARMIES_NEW_REGION_ASSIGN_ALGORITHM = 24						-- Max. amount of armies to use in new region assignment algorithm (fall back to old one).
																				-- Rosth: this value determines the maximum desired number of AI in a given region. "Region" refers to AI regions visible if you type "mapmode airegion" in the console. Based on what has been said in ACCEPTABLE_BALANCE_DEFAULT about the fact that the evaluation score heavily favors big stacks, lowering this would make the AI "doomstack" more but also potentially suffer more attrition. Vanilla value is 12.
NDefines.NAI.MAX_TASKS_NEW_REGION_ASSIGN_ALGORITHM = 200						-- Max. amount of tasks to use in new region assignment algorithm (fall back to old one).
																				-- Rosth: higher value means more frequent reassessment of AI stack movement orders. "Region" refers to AI regions visible if you type "mapmode airegion" in the console. Vanilla value is 100. This would in all likelihood not affect AI behaviour as it would be hard for the AI to issue all of these tasks all at the same time.
NDefines.NAI.ELECTORAL_REFORMATION_CONVERT_TRESHOLD = 0.1						-- How much of an electors development needs to be a reformed religion for it to try and reform
NDefines.NAI.TRADE_COMPANY_INVESTMENT_COST_THRESHOLD = 1						-- How many times the cost of the investment must be in the treasury to consider buying it
NDefines.NAI.ASSIMILATION_INTEREST_AMOUNT_FACTOR = 50							-- Influence on assimilation interest from number of provinces left to conquer.
																				-- Rosth: as the AI cannot pick any other colonial policy other than native coexistence, I am assuming it doesn't refer to the native trading policy and instead refers to consolidation of cultures/culture groups. At worst it does nothing, at best it would make the AI more interested in conquering land that has the same culture to its own.
NDefines.NAI.INVASION_ARMY_LOOKUP_INTERVAL_ON_FAILURE = 5						-- If AI fails to find an army for an invasion it will try again in this number of days
NDefines.NAI.CHARTER_COMPANY_BASE_RELUCTANCE = 10000							-- Base reluctance to giving away provinces in charter company diplo action
NDefines.NAI.CHARTER_COMPANY_DEVELOPMENT_RELUCTANCE = 0							-- How much development needed to add one reluctance
NDefines.NAI.WAR_WARSCORE_TO_JOIN = -100										-- Minimum warscore for the AI to join a call for arms
NDefines.NAI.WAR_MIN_WARSCORE_TO_JOIN = -25										-- Starting warscore for the AI to join a call for arms
NDefines.NAI.ARMY_DISTANCE_SCORE_IMPACT = 0.625 								-- Army <-> province distance impact on province evaluation.
																				-- Rosth: makes the AI less likely to run around like a moron. Inverse of this number (1/x) is multiplied by the distance value, which can be seen by selecting an AI stack, typing "mapmode aieval" in the console and hovering over a province. The lower the evaluation score for a province, the higher the AI's stack desire to move to it (applies to teal-colored provinces). This means that lowering this define would make the distance score value higher, which in turn would make that stack prioritise provinces that are closer to it over the ones that are further away. Vanilla value is 0.5.
NDefines.NAI.BORDER_DISTANCE_SCORE_IMPACT = 2.5 								-- Border <-> province distance impact on province evaluation.
																				-- Rosth: same principle as described above, only here the distance value is multiplied directly by this number. The further the province is from the AI's border, the less desireable it is. Vanilla value is 3.
NDefines.NAI.IMPORANT_PROVINCE_THRESHOLD = 0.015 								-- AI will try to defend provinces under threat worth more than this percentage of total development. Vanilla value is 0.05.
NDefines.NAI.REGION_PLANNING_HOMELAND_PRIORIZATION = 3							-- Homeland priorization when assigning armies to regions (only applied when actually threatened). Vanilla value is 3.5
																				-- Rosth: I'm assuming "homeland" either means "non-overseas provinces" or AI region that has it's capital.
NDefines.NAI.MISSIONARY_MAINTENANCE_SHARE = 0.2									-- The most an AI will spend of its income on a missionary
